		__BaseStrategy_init(_vault, "MOCK", "MockHedgedLP");

		// main strategy  should allways be intialized last
		__HedgedLP_init_unchained(_underlying, _short, type(uint256).max);
	}

	modifier checkPrice() override(HedgedLP) {
		_;
