src/strategies/HedgedLP.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

import "../mixins/IBase.sol";
import "../mixins/ILending.sol";
import "../mixins/IFarmableLp.sol";
import "../mixins/IUniLp.sol";
import "./BaseStrategy.sol";
import "../interfaces/uniswap/IWETH.sol";

import "hardhat/console.sol";

// @custom: alphabetize dependencies to avoid linearization conflicts
abstract contract HedgedLP is Initializable, IBase, BaseStrategy, ILending, IFarmableLp, IUniLp {
	using UniUtils for IUniswapV2Pair;
	using SafeERC20 for IERC20;

	uint256 private _maxPriceMismatch;

	modifier checkPrice() virtual {
		uint256 minPrice = _shortToUnderlying(1e18);
		// oraclePrice
		uint256 maxPrice = _oraclePriceOfShort(1e18);
		(minPrice, maxPrice) = maxPrice > minPrice ? (minPrice, maxPrice) : (maxPrice, minPrice);
		require(
			((maxPrice - minPrice) * BPS_ADJUST) / maxPrice < _maxPriceMismatch,
			"HedgedLP: SHORT_PRICE_MISMATCH"
		);
		_;
		// any method that uses checkPrice should updated the _cachedBalanceOfUnderlying
		(_cachedBalanceOfUnderlying, , , , , ) = getTVL();
	}

	IERC20 private _underlying;
	IERC20 private _short;

	uint256 _minLendHarvest; // don't harvest until we have this balance
	uint16 public rebalanceThreshold; // 4% of lp

	uint256 private _maxTvl;
	// for security we update this value only after oracle price checks in 'getAndUpdateTvl'
	uint256 private _cachedBalanceOfUnderlying;

	function __HedgedLP_init_unchained(
		address underlying_,
		address short_,
		uint256 maxTvl_
	) internal onlyInitializing {
		_underlying = IERC20(underlying_);
		_short = IERC20(short_);

		_underlying.safeApprove(address(this), type(uint256).max);
		// init params
		_maxPriceMismatch = 60; // .6% based on uniswap .6% bid-ask spread
		rebalanceThreshold = 400;
		_minLendHarvest = 4 * 1e6; // $4
		BASE_UNIT = 10**decimals();
		_maxTvl = maxTvl_;

		// TODO should this be a separate admin func?
		// TODO revoke aprovals methods?
		_addLendingApprovals();
		_addFarmApprovals();
	}

	// UPGRADE
	function onUpgrade(address farmRouter, address lendRounter) external {
		_maxTvl = type(uint256).max;
		_onUpgradeFarm(farmRouter);
		_onUpgradeLend(lendRounter);
		emit Upgrade();
	}

	function decimals() public view returns (uint8) {
		return IERC20Metadata(address(_underlying)).decimals();
	}

	// OWNER CONFIG

	function setMinLendHarvest(uint256 minLendHarvest_) public onlyOwner {
		_minLendHarvest = minLendHarvest_;
	}

	function setMaxPriceMismatch(uint256 maxPriceMismatch_) public onlyOwner {
		_maxPriceMismatch = maxPriceMismatch_;
	}

	function setRebalanceThreshold(uint16 rebalanceThreshold_) public onlyOwner {
		rebalanceThreshold = rebalanceThreshold_;
	}

	function setMaxTvl(uint256 maxTvl_) public onlyOwner {
		_maxTvl = maxTvl_;
	}

	// PUBLIC METHODS

	function short() public view override returns (IERC20) {
		return _short;
	}

	function underlying() public view override returns (IERC20) {
		return _underlying;
	}

	function _deposit(uint256 amount)
		internal
		override
		checkPrice
		nonReentrant
		returns (uint256 newShares)
	{
		uint256 tvl = _getAndUpdateTVL();
		require(amount + tvl < getMaxTvl(), "HedgedLP: OVER_MAX_TVL");
		newShares = totalSupply() == 0 ? amount : (totalSupply() * amount) / tvl;
		_underlying.transferFrom(vault(), address(this), amount);
		_increasePosition(amount);
		emit Deposit(msg.sender, amount);
	}

	function _withdraw(uint256 amount)
		internal
		override
		onlyVault
		checkPrice
		nonReentrant
		returns (uint256 burnShares)
	{
		uint256 tvl = _getAndUpdateTVL();
		uint256 reserves = _underlying.balanceOf(address(this));

		// if we can not withdraw straight out of reserves
		if (reserves < amount) {
			// add .5% to withdraw amount for tx fees & slippage etc
			uint256 withdrawAmnt = min(tvl, (amount * 1005) / 1000);

			if (withdrawAmnt >= tvl) {
				// decrease current position
				_closePosition();
				withdrawAmnt = _underlying.balanceOf(address(this));
			} else withdrawAmnt = _decreasePosition(withdrawAmnt - reserves) + reserves;
			// use the minimum of the two
			amount = min(withdrawAmnt, amount);
		}
		// grab current tvl to account for fees and slippage
		tvl = balanceOfUnderlying();
		// round up to keep price precision and leave less dust
		burnShares = min(((amount) * totalSupply()) / tvl + 1, tvl);

		_underlying.safeTransferFrom(address(this), vault(), amount);
		emit Withdraw(msg.sender, amount);
	}

	// decreases position based on current desired balance
	// ** does not rebalance remaining portfolio
	// ** may return slighly less then desired amount
	// ** make sure to update lending positions before calling this
	function _decreasePosition(uint256 amount) internal virtual returns (uint256) {
		uint256 removeLpAmnt = _totalToLp(amount);

		(uint256 underlyingLp, ) = _getLPBalances();

		uint256 shortPosition = _getBorrowBalance();

		// remove lp
		(uint256 underlyingBalance, uint256 shortBalance) = _decreaseLpTo(
			underlyingLp - removeLpAmnt
		);

		uint256 repayAmnt = shortBalance;

		if (shortPosition < shortBalance) {
			// this means we are closing the short position
			underlyingBalance += pair()._swapExactTokensForTokens(
				shortBalance - shortPosition,
				address(_short),
				address(_underlying)
			);
			repayAmnt = shortPosition;
		}
		_repay(repayAmnt);

		// this might remove less collateral than desired if we hit the limit
		// this happens when position is close to empty
		uint256 removed = _removeCollateral(amount - underlyingBalance);
		return underlyingBalance + removed;
	}

	// increases the position based on current desired balance
	// ** does not rebalance remaining portfolio
	function _increasePosition(uint256 amount) internal virtual {
		uint256 amntUnderlying = _totalToLp(amount);

		uint256 amntShort = _underlyingToShort(amntUnderlying);
		_lend(amount - amntUnderlying);
		_borrow(amntShort);
		uint256 liquidity = _addLiquidity(amntUnderlying, amntShort);
		_depositIntoFarm(liquidity);
	}

	// use the return of the function to estimate pending harvest via staticCall
	function harvest(
		HarvestSwapParms[] calldata uniParams,
		HarvestSwapParms[] calldata lendingParams
	)
		external
		onlyAuth
		checkPrice
		nonReentrant
		returns (uint256[] memory farmHarvest, uint256[] memory lendHarvest)
	{
		uint256 startTvl = _getAndUpdateTVL();

		if (uniParams.length != 0) farmHarvest = _harvestFarm(uniParams);
		if (lendingParams.length != 0) lendHarvest = _harvestLending(lendingParams);

		// deposit funds back into farm
		uint256 underlyingBal = _underlying.balanceOf(address(this));
		if (underlyingBal > 0) _lend(underlyingBal);
		uint256 shortBal = _short.balanceOf(address(this));
		if (shortBal > 0) _repay(shortBal);
		uint256 endTvl = balanceOfUnderlying();

		emit Harvest(endTvl > startTvl ? (endTvl - startTvl) : 0);
	}

	// MANAGER + OWNER METHODS

	function rebalance() public onlyAuth checkPrice nonReentrant {
		(uint256 underlyingLp, ) = _getLPBalances();
		uint256 tvl = _getAndUpdateTVL();
		uint256 targetUnderlyingLP = _totalToLp(tvl);

		(bool needsRebalance, bool shouldIncrease, uint256 positionOffset) = _shouldRebalance(
			targetUnderlyingLP,
			underlyingLp
		);
		require(needsRebalance, "HedgedLP: have not reached rebalance threshold"); // maybe next time...

		uint256 targetCollateral = tvl - targetUnderlyingLP;

		if (shouldIncrease)
			// this means lp is low - short price went down
			// most of the time we should not have to remove collateral
			_rebalanceUp(targetUnderlyingLP, targetCollateral);
			// this means lp is too high - short price went up
		else _rebalanceDown(targetUnderlyingLP, targetCollateral);
		emit Rebalance(_shortToUnderlying(1e18), positionOffset, tvl);
	}

	function closePosition() external onlyAuth {
		_closePosition();
	}

	function _closePosition() internal virtual {
		_decreaseLpTo(0);
		uint256 shortPosition = _updateAndGetBorrowBalance();
		uint256 shortBalance = _short.balanceOf(address(this));
		if (shortPosition >= shortBalance) {
			uint256 adjustShort = shortPosition - shortBalance;
			pair()._swapTokensForExactTokens(adjustShort, address(_underlying), address(_short));
		} else if (shortBalance > shortPosition) {
			uint256 adjustShort = shortBalance - shortPosition;
			pair()._swapExactTokensForTokens(adjustShort, address(_short), address(_underlying));
		}
		_repay(_short.balanceOf(address(this)));

		uint256 collateralBalance = _updateAndGetCollateralBalance();
		_redeem(collateralBalance);
	}

	function _shouldRebalance(uint256 targetUnderlyingLP, uint256 underlyingLp)
		internal
		view
		returns (
			bool needsRebalance,
			bool shouldIncrease,
			uint256 positionOffset
		)
	{
		shouldIncrease = targetUnderlyingLP > underlyingLp ? true : false;
		if (underlyingLp == 0 || targetUnderlyingLP == 0)
			return (underlyingLp != targetUnderlyingLP, shouldIncrease, positionOffset);

		uint256 shortPosition = _getBorrowBalance();

		// this is the % by which our position has moved from beeing balanced
		positionOffset = shouldIncrease
			? ((targetUnderlyingLP - underlyingLp) * BPS_ADJUST) / underlyingLp
			: ((underlyingLp - targetUnderlyingLP) * BPS_ADJUST) / targetUnderlyingLP;

		// don't rebalance unless
		needsRebalance =
			positionOffset > rebalanceThreshold ||
			_underlying.balanceOf(address(this)) > 10e6 ||
			shortPosition == 0;
	}

	// TODO handle case for when lp is not 100% in farm?
	function _decreaseLpTo(uint256 targetUnderlyingLP)
		internal
		virtual
		returns (uint256 underlyingRemove, uint256 shortRemove)
	{
		(uint256 underlyingLp, ) = _getLPBalances();
		uint256 liquidity = _getLiquidity();
		uint256 underlyingLiquidity = (liquidity * targetUnderlyingLP) / underlyingLp;
		uint256 removeLp = liquidity - underlyingLiquidity;
		_withdrawFromFarm(removeLp);
		return _removeLiquidity(removeLp);
	}

	// remove collateral (short price moved down so target collateral is lower)
	// borrow short
	// sell extra short
	// add lp
	function _rebalanceUp(uint256 targetUnderlyingLP, uint256 targetCollateral) internal {
		_adjustCollateral(targetCollateral);

		// borrow
		uint256 targetShortPosition = _underlyingToShort(targetUnderlyingLP);
		uint256 shortPosition = _getBorrowBalance();

		_borrow(targetShortPosition - shortPosition);

		// sell extra short
		(uint256 underlyingLp, ) = _getLPBalances();

		uint256 buyUnderlying = targetUnderlyingLP -
			(_underlying.balanceOf(address(this)) + underlyingLp);
		if (buyUnderlying > 0)
			pair()._swapTokensForExactTokens(buyUnderlying, address(_short), address(_underlying));

		// we will have more underlying tokens as a result of the tx fees
		// so we use short balance to compute final lp amounts
		uint256 amntShort = _short.balanceOf(address(this));
		// uint256 amntUnderlying = _shortToUnderlying(amntShort);

		uint256 amntUnderlying = _shortToUnderlying(amntShort);
		uint256 balUnderlying = _underlying.balanceOf(address(this));
		if (balUnderlying < amntUnderlying) {
			amntUnderlying = balUnderlying;
			amntShort = _underlyingToShort(amntUnderlying);
		}

		// add liquidity
		// don't need to use min with underlying and short because we did oracle check
		// amounts are exact because we used swap price above
		uint256 liquidity = _addLiquidity(amntUnderlying, amntShort);

		// ape into farm
		_depositIntoFarm(liquidity);

		// TODO - might have leftover underlying tokens here
	}

	// remove lp
	// return borrow
	// remove collateral
	// buy back extra short
	// return extra short borrow
	function _rebalanceDown(uint256 targetUnderlyingLP, uint256 targetCollateral) internal {
		if (targetUnderlyingLP == 0) return _closePosition();

		// remove lp
		_decreaseLpTo(targetUnderlyingLP);

		uint256 shortBalance = _short.balanceOf(address(this));
		_repay(shortBalance);

		// if we're already over collateral threshold we may not have enought to buy back
		// full amount of short tokens
		(, uint256 removed) = _adjustCollateral(targetCollateral);

		// if we're withdrawing we may end up with extra $$
		// do the check here
		uint256 shortPosition = _updateAndGetBorrowBalance();
		uint256 adjustBorrow = shortPosition - _underlyingToShort(targetUnderlyingLP);

		if (adjustBorrow == 0) return;

		uint256 underlyingIn = pair()._getAmountIn(
			adjustBorrow,
			address(_underlying),
			address(_short)
		);
		uint256 underlyingBalance = _underlying.balanceOf(address(this));
		uint256 sellShort = underlyingIn < underlyingBalance ? underlyingIn : underlyingBalance;
		pair()._swapExactTokensForTokens(sellShort, address(_underlying), address(_short));

		shortBalance = _short.balanceOf(address(this));
		// return borrow
		_repay(shortBalance);
		if (targetCollateral > removed) _adjustCollateral(targetCollateral);
	}

	// TVL

	function getMaxTvl() public view override returns (uint256) {
		console.log("max borrow", _maxBorrow(), _borrowToTotal(_oraclePriceOfShort(_maxBorrow())));
		return min(_maxTvl, _borrowToTotal(_oraclePriceOfShort(_maxBorrow())));
	}

	// TODO should we compute pending farm & lending rewards here?
	function _getAndUpdateTVL() internal virtual returns (uint256 tvl) {
		uint256 collateralBalance = _updateAndGetCollateralBalance();
		uint256 shortPosition = _updateAndGetBorrowBalance();
		uint256 borrowBalance = _shortToUnderlying(shortPosition);
		uint256 shortP = _short.balanceOf(address(this));
		uint256 shortBalance = shortP == 0
			? 0
			: _oraclePriceOfShort(_short.balanceOf(address(this)));
		(uint256 underlyingLp, ) = _getLPBalances();
		uint256 underlyingBalance = _underlying.balanceOf(address(this));
		tvl =
			collateralBalance +
			underlyingLp *
			2 -
			borrowBalance +
			underlyingBalance +
			shortBalance;
	}

	// for security this method should return cached value only
	// this is used by vault to track balance,
	// so this value should only be updated after oracle price check
	function balanceOfUnderlying() public view virtual override returns (uint256) {
		return _cachedBalanceOfUnderlying;
	}

	function getTVL()
		public
		view
		returns (
			uint256 tvl,
			uint256 collateralBalance,
			uint256 shortPosition,
			uint256 borrowBalance,
			uint256 lpBalance,
			uint256 underlyingBalance
		)
	{
		collateralBalance = _getCollateralBalance();
		shortPosition = _getBorrowBalance();

		uint256 shortP = _short.balanceOf(address(this));
		uint256 shortBalance = shortP == 0 ? 0 : _oraclePriceOfShort(shortP);

		borrowBalance = _shortToUnderlying(shortPosition);
		(uint256 underlyingLp, uint256 shortLp) = _getLPBalances();
		lpBalance = underlyingLp + _shortToUnderlying(shortLp);
		underlyingBalance = _underlying.balanceOf(address(this));

		tvl = collateralBalance + lpBalance - borrowBalance + underlyingBalance + shortBalance;
	}

	// UTILS

	function _totalToLp(uint256 total) internal view returns (uint256) {
		uint256 cRatio = getCollateralRatio();
		return (total * cRatio) / (BPS_ADJUST + cRatio);
	}

	function _borrowToTotal(uint256 amount) internal view returns (uint256) {
		uint256 cRatio = getCollateralRatio();
		return (amount * (BPS_ADJUST + cRatio)) / cRatio;
	}

	// this is the current uniswap price
	function _shortToUnderlying(uint256 amount) internal view returns (uint256) {
		return amount == 0 ? 0 : _quote(amount, address(_short), address(_underlying));
	}

	// this is the current uniswap price
	function _underlyingToShort(uint256 amount) internal view returns (uint256) {
		return amount == 0 ? 0 : _quote(amount, address(_underlying), address(_short));
	}

	/**
	 * @dev Returns the smallest of two numbers.
	 */
	function min(uint256 a, uint256 b) internal pure returns (uint256) {
		return a < b ? a : b;
	}

	uint256[49] private _gap;
}

