// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "../../mixins/ILending.sol";
import "../../mixins/IUniLp.sol";

abstract contract MockLending is ILending {
	uint256 borrowAmount = 0;
	uint256 lendAmount = 0;
	uint256 exchangeRate = 2.5e18;
	uint256 lpShort = 0;
	uint256 lpUnderlying = 0;

	// _deposit
	// _getAndUpdateTVL
	//  totalSupply()
	// _increasePosition
	// _totalToLp

	function getCollateralRatio() public view virtual override returns (uint256) {
		// maximum amount you can lp
		return 0.8e18;
	}

	function _getCollateralBalance() internal view virtual override returns (uint256) {
		return ((lendAmount * exchangeRate) / 1e18);
	}

	function _addLiquidity(uint256 amtUnderlying, uint256 amtShort)
		internal
		virtual
		returns (uint256)
	{
		lpUnderlying += amtUnderlying;
		lpShort += amtShort;
	}

	function _depositIntoFarm(HarvestSwapParms[] memory params) internal virtual {}

	function _quote(
		uint256 amount,
		address underlying,
		address short
	) internal view virtual returns (uint256) {
		return ((amount * exchangeRate) / 1e18);
	}

	function _lend(uint256 amount) internal virtual override {
		lendAmount += amount;
	}

	function _redeem(uint256 amount) internal virtual override {
		require(lendAmount >= amount, "Amount exceeds lend balance");
		lendAmount -= amount;
	}

	function _borrow(uint256 amount) internal virtual override {
		borrowAmount = borrowAmount + amount;
	}

	function _repay(uint256 amount) internal virtual override {
		require(borrowAmount >= amount, "Amount exceeds borrow balance");
		borrowAmount -= amount;
	}

	function _getBorrowBalance() internal view virtual override returns (uint256) {
		return borrowAmount;
	}

	function _getLPBalances()
		internal
		view
		virtual
		returns (uint256 underlyingBalance, uint256 shortBalance)
	{
		underlyingBalance = lpUnderlying;
		shortBalance = lpShort;
	}

	function _decreaseLpTo(uint256 targetUnderlyingLp)
		internal
		virtual
		returns (uint256 underlyingRemove, uint256 shortRemove)
	{
		uint256 removeFraction = ((lpUnderlying - targetUnderlyingLp) * 1e18) / lpUnderlying;
		return _removeLiquidity(removeFraction);
	}

	function _removeLiquidity(uint256 liquidity) internal virtual returns (uint256, uint256) {
		uint256 removeUnderlying = (lpUnderlying * liquidity) / 1e18;
		lpUnderlying -= removeUnderlying;

		uint256 removeShort = (lpShort * liquidity) / 1e18;
		lpShort -= removeShort;

		return (removeUnderlying, removeShort);
	}

	// function _swapExactTokensForTokens(
	// 	IUniswapV2Pair uniPair,
	// 	uint256 amountIn,
	// 	address inToken,
	// 	address outToken
	// ) public virtual returns (uint256) {
	// 	// IERC20(inToken).safeTransfer(address(pair), amountIn);
	// }

	function _oraclePriceOfShort(uint256 amount) internal view virtual override returns (uint256) {
		return exchangeRate;
	}
}

