pragma solidity ^0.8.2;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "../HedgedLP.sol";
import "../modules/Compound.sol";
import "../modules/MasterChefFarm.sol";
import "../modules/CompoundFarm.sol";

// import "hardhat/console.sol";

contract USDCftmSPOOKYscream is Initializable, HedgedLP, Compound, CompoundFarm, MasterChefFarm {
	uint256[200] private _gap;

	// @custom:oz-upgrades-unsafe-allow constructor
	constructor() initializer {}

	function initialize(Config memory config) public initializer {
		__MasterChefFarm_init_unchained(
			config.uniPair,
			config.uniFarm,
			config.farmRouter,
			config.farmToken,
			config.farmId
		);

		__Compound_init_unchained(
			config.comptroller,
			config.cTokenLend,
			config.cTokenBorrow,
			config.safeCollateralRatio
		);

		__CompoundFarm_init_unchained(config.lendRewardRouter, config.lendRewardToken);

		__BaseStrategy_init(config.vault, config.symbol, config.name);

		// main strategy  should allways be intialized last
		__HedgedLP_init_unchained(config.underlying, config.short, config.maxTvl);

		version = 1;
	}

	receive() external payable {}

	// our borrow token is treated as ETH by benqi
	function _isBase(uint8) internal pure override(ICompound) returns (bool) {
		return false;
	}

	uint256 public version;
}

