		__BaseStrategy_init(config.vault, config.symbol, config.name);

		// main strategy  should allways be intialized last
		__HedgedLP_init_unchained(config.underlying, config.short, config.maxTvl);

		version = 1;
	}

	receive() external payable {}

	// our borrow token is treated as ETH by benqi
	function _isBase(uint8 id) internal pure override(ICompound) returns (bool) {
		return id == 1 ? true : false;
	}

