// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/draft-ERC20PermitUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";

// import "hardhat/console.sol";

abstract contract BaseStrategy is
	Initializable,
	ERC20Upgradeable,
	ERC20PermitUpgradeable,
	AccessControlUpgradeable,
	ReentrancyGuardUpgradeable
{
	using SafeERC20 for IERC20;

	uint256 constant BPS_ADJUST = 10000;
	uint256 private constant SHARE_DECIMALS = 1e18;

	bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");
	bytes32 public constant WHITELIST_ROLE = keccak256("WHITELIST_ROLE");

	// upper limit for fees so owner cannot maliciously increase fees TODO - used when not upgradable
	uint256 public constant operationalFeeLimit = 500; // 5% limit
	uint256 public constant profitFeeLimit = 20000; // 20% limit

	// Vault limits TODO use these
	uint256 public accountDepositLimit; // max deposit per account
	uint256 public vaultTvlLimit; // max vault TVL. if this is reached, deposits will fail.

	// these need initialization
	uint256 public sharePriceTracker;
	uint256 public operationalFee;
	uint256 public profitFee;
	address public rewardsAddr;
	uint256 public lastHarvest; // block.timestamp;

	event UpdatedManager(address newManager);
	event Harvested(uint256 harvested, uint256 profitFeeTaken, uint256 operationalFeeTaken);
	event Deposit(address addr, uint256 want, uint256 shares);
	event Withdrawal(address addr, uint256 want, uint256 shares, uint256 fee);
	event UpdatedRewardsAddr(address rewardsAddr);

	function __BaseStrategy_init_unchained(
		address rewardsAddr_,
		uint256 operationalFee_,
		uint256 profitFee_,
		address manager
	) internal initializer {
		require(
			operationalFee_ < operationalFeeLimit,
			"BaseStrategy: operationalFee exceeds limit"
		);
		require(profitFee_ < profitFeeLimit, "BaseStrategy: profitFee exceeds limit");

		__ReentrancyGuard_init_unchained();

		_setupRole(MANAGER_ROLE, manager);
		_setupRole(WHITELIST_ROLE, manager);
		_setupRole(WHITELIST_ROLE, msg.sender);

		rewardsAddr = rewardsAddr_;
		operationalFee = operationalFee_;
		profitFee = profitFee_;

		// init defaults
		sharePriceTracker = SHARE_DECIMALS; // init at 1
		lastHarvest = block.timestamp;
		accountDepositLimit = type(uint256).max; // max deposit per account
		vaultTvlLimit = type(uint256).max; // max vault TVL. if this is reached, deposits will fail.
	}

	/**
	 * @notice
	 *  Used to change `rewards`. EOA or smart contract which has the permission
	 *  to pull rewards from the vault.
	 *
	 *  This may only be called by the manager.
	 * @param rewardsAddr_ The address to use for pulling rewards.
	 */
	// function setRewards(address rewardsAddr_) external onlyOwner {
	function setRewards(address rewardsAddr_) external onlyRole(MANAGER_ROLE) {
		require(rewardsAddr_ != address(0));
		rewardsAddr_ = rewardsAddr_;
		emit UpdatedRewardsAddr(rewardsAddr_);
	}

	/**
	 * @notice
	 *  Provide an accurate estimate for the total amount of assets
	 *  (principle + return) that this Strategy is currently managing,
	 *  denominated in terms of `want` tokens.
	 *
	 *  This total should be "realizable" e.g. the total value that could
	 *  *actually* be obtained from this Strategy if it were to divest its
	 *  entire position wantd on current on-chain conditions.
	 * @dev
	 *  Care must be taken in using this function, since it relies on external
	 *  systems, which could be manipulated by the attacker to give an inflated
	 *  (or reduced) value produced by this function, wantd on current on-chain
	 *  conditions (e.g. this function is possible to influence through
	 *  flashloan attacks, oracle manipulations, or other DeFi attack
	 *  mechanisms).
	 *
	 * @return The estimated total assets in this Strategy.
	 */
	function estimatedTotalAssets() public view virtual returns (uint256);

	/**
	 * @notice
	 *  Harvests the Strategy
	 *
	 *  This may only be called by MANAGER_ROLE.
	 * @dev
	 *  When `harvest()` is called, the Strategy reports to the Vault (via
	 *  `vault.report()`), so in some cases `harvest()` must be called in order
	 *  to take in profits, to borrow newly available funds from the Vault, or
	 *  otherwise adjust its position. In other cases `harvest()` must be
	 *  called to report to the Vault on the Strategy's position, especially if
	 *  any losses have occurred.
	 */
	// function harvest() external onlyOwner {
	function harvest() external onlyRole(MANAGER_ROLE) {
		// harvest
		uint256 harvested = _harvestInternal();

		// Take fees
		(uint256 pFee, uint256 oFee) = takeFees();

		// Update harvest timestamp - used for fee calculations
		lastHarvest = block.timestamp;

		emit Harvested(harvested, pFee, oFee);
	}

	/**
	 * Virtual function for triggering a harvest
	 *
	 * Returns
	 * want harvested in harvest
	 */
	function _harvestInternal() internal virtual returns (uint256 _wantHarvested);

	/**
	 * @notice
	 *  Returns the share price of the strategy in `want` units, multiplied
	 *  by 1e18
	 */
	function getPricePerShare() public view returns (uint256) {
		uint256 bal = estimatedTotalAssets();
		uint256 supply = totalSupply();
		return (bal * SHARE_DECIMALS) / supply;
	}

	/**
	 * @notice
	 *  Takes the fees from the vault and distributes it to manager selected `rewards` address.
	 *   - Fees are issued as minted shares.
	 *
	 *  Profit Fee:
	 *  A percentage fee take on profits.
	 *
	 *  Operational Fee:
	 *  The operational fee is a percentage of TVL taken incrementally each harvest
	 *  over 365 days.
	 */
	function takeFees() internal returns (uint256 _profitFeeMinted, uint256 _operationFeeMinted) {
		uint256 pricePerShare = getPricePerShare();
		// only take performance fee on profits
		if (pricePerShare > sharePriceTracker) {
			uint256 profitPerShare = pricePerShare - sharePriceTracker;
			uint256 profitPercent = (profitPerShare * SHARE_DECIMALS) / sharePriceTracker;
			_profitFeeMinted =
				(totalSupply() * profitPercent * profitFee) /
				BPS_ADJUST /
				SHARE_DECIMALS;

			// uint256 profitFeeGov = (_profitFeeMinted * profitFeeGovernance) / BPS_ADJUST;
			// uint256 profitFeeStrat = _profitFeeMinted - profitFeeGov;
			// _mint(rewards, profitFeeStrat);
			// _mint(governance, profitFeeGov);
			_mint(rewardsAddr, _profitFeeMinted);
			sharePriceTracker = pricePerShare;
		}

		if (operationalFee != 0) {
			// take operational fee
			uint256 timeSinceHarvest = block.timestamp - lastHarvest;
			uint256 annualAdj = uint256(365) * 24 * 60 * 60;
			_operationFeeMinted =
				(operationalFee * timeSinceHarvest * totalSupply()) /
				BPS_ADJUST /
				annualAdj;
			_mint(rewardsAddr, _operationFeeMinted);
		}
	}

	uint256[50] private _gap;
}

