src/HedgedLP.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "./mixins/ILending.sol";
import "./mixins/IFarmable.sol";
import "./mixins/IUniLp.sol";
import "./mixins/IBase.sol";
import "./mixins/ISwappable.sol";
import "./BaseStrategy.sol";
import "./interfaces/uniswap/IWETH.sol";

// import "hardhat/console.sol";

// @custom: alphabetize dependencies to avoid linearization conflicts
abstract contract HedgedLP is
	Initializable,
	BaseStrategy,
	IBase,
	IFarmable,
	ILending,
	ISwappable,
	IUniLp
{
	using SafeERC20 for IERC20;

	uint256 private _maxPriceMismatch;

	modifier checkPrice() {
		uint256 minPrice = _quote(1e18, address(_short), address(_want));
		uint256 maxPrice = _oraclePriceOfShort(1e18);
		(minPrice, maxPrice) = maxPrice > minPrice ? (minPrice, maxPrice) : (maxPrice, minPrice);
		require(
			((maxPrice - minPrice) * BPS_ADJUST) / maxPrice < _maxPriceMismatch,
			"HedgedLP: SHORT_PRICE_MISMATCH"
		);
		_;
	}

	IERC20 private _want;
	IERC20 private _short;

	uint256 _minLendHarvest; // don't harvest until we have this balance
	uint16 public rebalanceThreshold; // 4% of lp

	function __HedgedLP_init_unchained(address want_, address short_) internal initializer {
		_want = IERC20(want_);
		_short = IERC20(short_);

		_want.safeApprove(address(this), type(uint256).max);

		// init params
		_maxPriceMismatch = 60; // .6% based on uniswap .6% bid-ask spread
		rebalanceThreshold = 400;
		_minLendHarvest = 20 * 1e6;
	}

	// PUBLIC METHODS

	// TODO uncomment later
	// function updateParams(
	// 	uint256 minLendHarvest_,
	// 	uint256 maxPriceMismatch_,
	// 	uint16 rebalanceThreshold_
	// ) public onlyRole(MANAGER_ROLE) {
	// 	_minLendHarvest = minLendHarvest_;
	// 	_maxPriceMismatch = maxPriceMismatch_;
	// 	rebalanceThreshold = rebalanceThreshold_;
	// }

	function short() public view override returns (IERC20) {
		return _short;
	}

	function want() public view override returns (IERC20) {
		return _want;
	}

	// assets are deposited but rebalance needs to be called before assets are deployed
	// TODO can we make it safe to call rebalance here?
	function deposit(uint256 total) external onlyRole(WHITELIST_ROLE) checkPrice nonReentrant {
		uint256 tvl = _getAndUpdateTVL();
		uint256 shares = totalSupply() == 0 ? total : (totalSupply() * total) / tvl;
		_want.transferFrom(msg.sender, address(this), total);
		_increasePosition(total);
		_mint(msg.sender, shares);
		emit Deposit(msg.sender, total, shares);
	}

	function withdraw(uint256 shares) external checkPrice nonReentrant {
		uint256 tvl = _getAndUpdateTVL();
		uint256 withdrawAmnt = (tvl * shares) / totalSupply();
		uint256 reserves = _want.balanceOf(address(this));

		// if we can not withdraw straight out of reserves
		if (reserves < withdrawAmnt) {
			if (shares == totalSupply()) {
				// decrease current position
				_closePosition();
				withdrawAmnt = _want.balanceOf(address(this));
			} else withdrawAmnt = _decreasePosition(withdrawAmnt - reserves) + reserves;
		}
		shares = (withdrawAmnt * totalSupply()) / tvl;
		_burn(msg.sender, shares);
		_want.safeTransferFrom(address(this), msg.sender, withdrawAmnt);
		emit Withdrawal(msg.sender, withdrawAmnt, shares, 0);
	}

	// decreases position based on current desired balance
	// ** does not rebalance remaining portfolio
	// ** may return slighly less then desired amount
	// ** make sure to update lending positions before calling this
	function _decreasePosition(uint256 amount) internal returns (uint256) {
		uint256 removeLpAmnt = _totalToLp(amount);

		(uint256 wantLp, ) = _getLPBalances();
		// uint256 removeLpAmnt = (amount * wantLp) / position;

		uint256 shortPosition = _getBorrowBalance();

		// remove lp
		(uint256 wantBalance, uint256 shortBalance) = _decreaseLpTo(wantLp - removeLpAmnt);
		uint256 repayAmnt = shortBalance;
		if (shortPosition < shortBalance) {
			// this means we are closing the short position
			wantBalance += _swapExactTokensForTokens(
				pair(),
				shortBalance - shortPosition,
				address(_short),
				address(_want)
			);
			repayAmnt = shortPosition;
		}
		_repay(repayAmnt);

		// this might remove less collateral than desired if we hit the limit
		// this happens when position is close to empty
		uint256 removed = _removeCollateral(amount - wantBalance);
		return wantBalance + removed;
	}

	// increases the position based on current desired balance
	// ** does not rebalance remaining portfolio
	function _increasePosition(uint256 amount) internal {
		uint256 amntWant = _totalToLp(amount);
		uint256 amntShort = _quote(amntWant, address(_want), address(_short));
		_lend(amount - amntWant);
		_borrow(amntShort);
		uint256 liquidity = _addLiquidity(amntWant, amntShort);
		_depositIntoFarm(liquidity);
	}

	function _harvestInternal() internal override checkPrice returns (uint256 harvested) {
		harvested += _harvestLending(_minLendHarvest);
		harvested += _harvestFarm();
		_increasePosition(_want.balanceOf(address(this)));
	}

	// MANAGER METHODS
	// TODO can we make it safe to make these public?

	function rebalance() public onlyRole(MANAGER_ROLE) checkPrice nonReentrant {
		(uint256 wantLp, ) = _getLPBalances();
		uint256 tvl = _getAndUpdateTVL();
		uint256 targetWantLP = _totalToLp(tvl);

		(bool needsRebalance, bool shouldIncrease) = _shouldRebalance(targetWantLP, wantLp);
		require(needsRebalance); // maybe next time...

		uint256 targetCollateral = tvl - targetWantLP;

		if (shouldIncrease)
			// this means lp is low - short price went down
			// most of the time we should not have to remove collateral
			_rebalanceUp(targetWantLP, targetCollateral);
			// this means lp is too high - short price went up
		else _rebalanceDown(targetWantLP, targetCollateral);
	}

	function closePosition() external onlyRole(MANAGER_ROLE) {
		_closePosition();
	}

	function _closePosition() internal {
		_decreaseLpTo(0);
		uint256 shortPosition = _updateAndGetBorrowBalance();
		uint256 shortBalance = _short.balanceOf(address(this));
		if (shortPosition > shortBalance) {
			uint256 adjustShort = shortPosition - shortBalance;
			_swapTokensForExactTokens(pair(), adjustShort, address(_want), address(_short));
		} else if (shortBalance > shortPosition) {
			uint256 adjustShort = shortBalance - shortPosition;
			_swapExactTokensForTokens(pair(), adjustShort, address(_short), address(_want));
		}
		_repay(_short.balanceOf(address(this)));

		uint256 collateralBalance = _updateAndGetCollateralBalance();
		_redeem(collateralBalance);
	}

	function _shouldRebalance(uint256 targetWantLP, uint256 wantLp)
		internal
		view
		returns (bool needsRebalance, bool shouldIncrease)
	{
		shouldIncrease = targetWantLP > wantLp ? true : false;
		if (wantLp == 0 || targetWantLP == 0) return (wantLp != targetWantLP, shouldIncrease);

		uint256 shortPosition = _getBorrowBalance();

		// this is the % by which our position has moved from beeing balanced
		uint256 positionOffset = shouldIncrease
			? ((targetWantLP - wantLp) * BPS_ADJUST) / wantLp
			: ((wantLp - targetWantLP) * BPS_ADJUST) / targetWantLP;

		// don't rebalance unless
		needsRebalance =
			positionOffset > rebalanceThreshold ||
			_want.balanceOf(address(this)) > 10e6 ||
			shortPosition == 0;
	}

	// TODO handle case for when lp is not 100% in farm?
	function _decreaseLpTo(uint256 targetWantLP)
		internal
		returns (uint256 wantRemove, uint256 shortRemove)
	{
		(uint256 wantLp, ) = _getLPBalances();
		uint256 liquidity = _getLiquidity();
		uint256 wantLiquidity = (liquidity * targetWantLP) / wantLp;
		uint256 removeLp = liquidity - wantLiquidity;
		_withdrawFromFarm(removeLp);
		return _removeLiquidity(removeLp);
	}

	// remove collateral (short price moved down so target collateral is lower)
	// borrow short
	// sell extra short
	// add lp
	function _rebalanceUp(uint256 targetWantLP, uint256 targetCollateral) internal {
		_adjustCollateral(targetCollateral);

		// borrow
		uint256 targetShortPosition = _wantToShort(targetWantLP);
		uint256 shortPosition = _getBorrowBalance();

		_borrow(targetShortPosition - shortPosition);

		// sell extra short
		(uint256 wantLp, ) = _getLPBalances();

		uint256 buyWant = targetWantLP - (_want.balanceOf(address(this)) + wantLp);
		if (buyWant > 0)
			_swapTokensForExactTokens(pair(), buyWant, address(_short), address(_want));

		// we will have more want tokens as a result of the tx fees
		// so we use short balance to compute final lp amounts
		uint256 amntShort = _short.balanceOf(address(this));
		uint256 amntWant = _shortToWant(amntShort);
		uint256 balWant = _want.balanceOf(address(this));
		if (balWant < amntWant) {
			amntWant = balWant;
			amntShort = _wantToShort(amntWant);
		}

		// add liquidity
		uint256 liquidity = _addLiquidity(amntWant, amntShort);

		// ape into farm
		_depositIntoFarm(liquidity);

		// TODO - might have leftover want tokens here
	}

	// remove lp
	// return borrow
	// remove collateral
	// buy back extra short
	// return extra short borrow
	function _rebalanceDown(uint256 targetWantLP, uint256 targetCollateral) internal {
		if (targetWantLP == 0) return _closePosition();

		// remove lp
		_decreaseLpTo(targetWantLP);

		uint256 shortBalance = _short.balanceOf(address(this));
		_repay(shortBalance);

		// if we're already over collateral threshold we may not have enought to buy back
		// full amount of short tokens
		(, uint256 removed) = _adjustCollateral(targetCollateral);

		// if we're withdrawing we may end up with extra $$
		// do the check here
		uint256 shortPosition = _updateAndGetBorrowBalance();
		uint256 adjustBorrow = shortPosition - _wantToShort(targetWantLP);

		if (adjustBorrow == 0) return;

		uint256 wantIn = UniUtils._getAmountIn(
			pair(),
			adjustBorrow,
			address(_want),
			address(_short)
		);
		uint256 wantBalance = _want.balanceOf(address(this));
		uint256 sellShort = wantIn < wantBalance ? wantIn : wantBalance;
		_swapExactTokensForTokens(pair(), sellShort, address(_want), address(_short));

		shortBalance = _short.balanceOf(address(this));
		// return borrow
		_repay(shortBalance);
		if (targetCollateral > removed) _adjustCollateral(targetCollateral);
	}

	function _totalToLp(uint256 total) internal view returns (uint256) {
		uint256 cRatio = getCollateralRatio();
		return (total * cRatio) / (BPS_ADJUST + cRatio);
	}

	// TODO should we compute pending farm & lending rewards here?
	function _getAndUpdateTVL() internal returns (uint256 tvl) {
		uint256 collateralBalance = _updateAndGetCollateralBalance();
		uint256 shortPosition = _updateAndGetBorrowBalance();
		uint256 shortBalance = _shortToWant(shortPosition);
		(uint256 wantLp, ) = _getLPBalances();
		uint256 wantBalance = _want.balanceOf(address(this));
		tvl = collateralBalance + wantLp * 2 - shortBalance + wantBalance;
	}

	function estimatedTotalAssets() public view override returns (uint256 assets) {
		(assets, , , , , ) = getTVL();
	}

	// VIEW
	function getTVL()
		public
		view
		returns (
			uint256 tvl,
			uint256 collateralBalance,
			uint256 shortPosition,
			uint256 shortBalance,
			uint256 lpBalance,
			uint256 wantBalance
		)
	{
		collateralBalance = _getCollateralBalance();
		shortPosition = _getBorrowBalance();
		// shortBalance is the short position denominated in want tokens
		shortBalance = _shortToWant(shortPosition);
		(uint256 wantLp, ) = _getLPBalances();
		lpBalance = wantLp * 2;
		wantBalance = _want.balanceOf(address(this));

		tvl = collateralBalance + lpBalance - shortBalance + wantBalance;
	}

	function _shortToWant(uint256 amount) internal view returns (uint256) {
		if (amount == 0) return 0;
		// lending oracle price
		// return _oraclePriceOfShort(amount);

		// uni price
		return _quote(amount, address(_short), address(_want));
	}

	function _wantToShort(uint256 amount) internal view returns (uint256) {
		if (amount == 0) return 0;
		// lending oracle price
		// return _oraclePriceOfWant(amount);

		// uni price
		return _quote(amount, address(_want), address(_short));
	}

	uint256[50] private _gap;
}

