// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "./HedgedLP.sol";
import "./strategy/Compound.sol";
import "./strategy/FarmableUniLp.sol";
import "./strategy/BenqiFarm.sol";

// import "hardhat/console.sol";

contract vUSDCavaxPB is Initializable, HedgedLP, Compound, BenqiFarm, FarmableUniLp {
	uint256[200] private _gap;

	struct Config {
		address want;
		address short;
		address cTokenLend;
		address cTokenBorrow;
		address uniPair;
		address uniFarm;
		address farmLp;
		address farmToken;
		address router;
		address comptroller;
		address benqiPair;
		address benqiToken;
		uint256 safeCollateralRatio;
		uint256 operationalFee;
		uint256 profitFee;
		address manager;
	}

	// @custom:oz-upgrades-unsafe-allow constructor
	// constructor() initializer {}

	function initialize(Config memory config) public initializer {
		__ERC20_init("vUSDCAVAXPangolinBenqi", "vUSDCPB");
		__ERC20Permit_init("vUSDCAVAXPangolinBenqi");
		__AccessControl_init();

		_setupRole(DEFAULT_ADMIN_ROLE, msg.sender);

		__FarmableUniLp_init_unchained(
			config.uniPair,
			config.uniFarm,
			config.farmLp,
			config.farmToken
		);
		__Compound_init_unchained(
			config.comptroller,
			config.cTokenLend,
			config.cTokenBorrow,
			config.safeCollateralRatio
		);
		__BenqiFarm_init_unchained(config.comptroller, config.benqiPair, config.benqiToken);

		__BaseStrategy_init_unchained(
			msg.sender,
			config.operationalFee,
			config.profitFee,
			config.manager
		);
		// main strategy  should allways be intialized last
		__HedgedLP_init_unchained(config.want, config.short);

		// TODO should this be a separate admin func?
		// TODO revoke aprovals methods?
		_addLendingApprovals();
		_addFarmApprovals();
	}

	receive() external payable {}

	// required implementations: hooks
	function _postBorrowHook(uint256 amount) internal override(Compound) {
		// wrap avax
		IWETH(address(short())).deposit{ value: amount }();
	}

	function _preRepayHook(uint256 amount) internal override(Compound) {
		// wrap avax
		IWETH(address(short())).withdraw(amount);
	}
}

