// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "../interfaces/forks/IBenqiComptroller.sol";
import "../mixins/ISwappable.sol";
import "../mixins/IUniLp.sol";
import "../mixins/ILending.sol";
import "../interfaces/uniswap/IUniswapV2Pair.sol";
import "../interfaces/uniswap/IWETH.sol";

// import "hardhat/console.sol";

abstract contract BenqiFarm is Initializable, ILending, ISwappable, IUniLp {
	IBenqiComptroller _comptroller;
	IERC20 _qi;
	IUniswapV2Pair private _qiAvaxPair;

	function __BenqiFarm_init_unchained(
		address comptroller_,
		address pair_,
		address token_
	) internal initializer {
		_comptroller = IBenqiComptroller(comptroller_);
		_qiAvaxPair = IUniswapV2Pair(pair_);
		_qi = IERC20(token_);
	}

	function _pendingLendingHarvest() internal view override returns (uint256 pendingWant) {
		uint256 pendingQi = _comptroller.rewardAccrued(0, address(this));

		uint256 pendingShort = pendingQi == 0
			? 0
			: UniUtils._getAmountOut(_qiAvaxPair, pendingQi, address(_qi), address(short()));
		pendingShort += _comptroller.rewardAccrued(1, address(this));
		pendingWant = pendingShort == 0
			? 0
			: UniUtils._getAmountOut(pair(), pendingShort, address(short()), address(want()));
	}

	function _harvestLending(uint256 minHarvest) internal override returns (uint256 harvested) {
		uint256 pending = _pendingLendingHarvest();
		if (pending < minHarvest) return harvested;

		// qi rewards
		_comptroller.claimReward(0, payable(address(this)));
		uint256 farmTokenBalance = _qi.balanceOf(address(this));

		if (farmTokenBalance > 0) {
			// qi - wavax pair is the most liquid
			_swapExactTokensForTokens(
				_qiAvaxPair,
				farmTokenBalance,
				address(_qi),
				address(short())
			);
		}

		// avax rewards
		_comptroller.claimReward(1, payable(address(this)));
		uint256 avaxBalance = address(this).balance;
		if (avaxBalance == 0) return harvested;

		IWETH(address(short())).deposit{ value: avaxBalance }();
		harvested = _swapExactTokensForTokens(
			pair(),
			avaxBalance,
			address(short()),
			address(want())
		);
	}

	uint256[50] private _gap;
}

